import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";
import RuleModal from "./RuleModal";
const AddRule = ({ show, handleClose }) => {
    const { projectById } = useSelector((state) => state.Project);
    const { status } = projectById;
    const { stageList } = projectById;
    const [formData, setFormData] = useState({
        previousStage: "",
        currentStage: "",
        condition: "",
        nextStatus: "",
    });
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };
    const handleCreateRule = () => {
        const selectedStage = stageList.find(
            (stage) => stage.stageName === formData.nextStatus
        );

        const newRule = {
            previousStageTrigger: formData.previousStage || "Any",
            currentStageTrigger: formData.currentStage || "Any",
            previousStageAction: formData.condition || "Any",
            currentStageAcion: formData.nextStatus || "Any",
            triggerType: "StageChange",
        };

        if (newRule.currentStageAcion === "Any") {
            window.confirm(
                "Please Select Next Status "
            );
            return;
        }
        axios
            .post(
                `http://localhost:8080/projects/${projectById.projectId}/rules`,
                newRule
            )
            .then((response) => {
                console.log("API call successful:", response.data.data);
            })
            .catch((error) => {
                console.error("Error updating data: ", error);
            });
        handleClose();
    };
    let modalTitle, modalContent;
    modalTitle = "Rules";
    modalContent = (
        <div>
            <div className="form-group">
                <label>TriggerType</label>
                <select
                    className="form-control"
                    name="previousStage"
                    value={formData.previousStage}
                    onChange={handleInputChange}
                >
                    <option key="Status_Change" value="Status_Change">
                    Status_Change
                    </option>
                    <option key="Stage_Change" value="Stage_Change">
                    Stage_Change
                    </option>
                    
                    <option key="Due_Date_Change" value="Due_Date_Change">
                    Due_Date_Change
                    </option>
                    
                    

                </select>
            </div>
            <div className="form-group">
                <label>ActionType </label>
                <select
                    className="form-control"
                    name="currentStage"
                    value={formData.currentStage}
                    onChange={handleInputChange}
                >
                    <option key="Change_Stage" value="Change_Stage">
                    Change_Stage
                    </option>
                    <option key="Change_Status" value="Change_Status">
                    Change_Status
                    </option>
                    
                    <option key="Change_Prority" value="Change_Prority">
                    Change_Prority
                    </option>
                </select>
            </div>
        </div>
    );
    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>{modalTitle}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {modalContent}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={handleCreateRule}>
                    Create
                </Button>
            </Modal.Footer>
        </Modal>
    );
};
export default AddRule
