import SockJS from 'sockjs-client';
import Stomp from 'stompjs';

const WebSocketService = {
    stompClient: null,

    connect: () => {
        const socket = new SockJS('http://localhost:8080/websocket-endpoint');
        WebSocketService.stompClient = Stomp.over(socket);

        WebSocketService.stompClient.connect({}, () => {
            console.log('Connected to WebSocket');
        });
    },

    subscribeToMessages: (callback) => {
        WebSocketService.stompClient.subscribe('/topic/messages', (response) => {
            const message = JSON.parse(response.body);
            callback(message);
        });
    },

    sendMessage: (message) => {
        WebSocketService.stompClient.send('/app/send-message', {}, JSON.stringify({ message }));
    },
};

export default WebSocketService;
