import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";

const AddRule = ({ show, handleClose }) => {
  const { projectById } = useSelector((state) => state.Project);
  const { status } = projectById;
  const { stageList } = projectById;
  const {projectId} = projectById;
  const [triggerType, setTriggerType] = useState("");
  const [actionType, setActionType] = useState("");
  const [additionalField, setAdditionalField] = useState("");
  const [ruleObject, setRuleObject] = useState(null);
  const handleTriggerTypeChange = (e) => {
    setTriggerType(e.target.value);
  };

  const handleActionTypeChange = (e) => {
    setActionType(e.target.value);
  };

  const handleCreateRule = () => {
    if (triggerType === "" || actionType === "") {
      return;
    }

    let newRuleObject = {
      triggerType: triggerType,
      actionType: actionType,
    };

    if (triggerType === "STATUS_CHANGE") {
      newRuleObject.statusCondition = {
        previousStatus: document.getElementById("previousStatusTrigger").value,
        currentStatus: document.getElementById("currentStatusTrigger").value,
      };
    } else if (triggerType === "STAGE_CHANGE") {
      newRuleObject.stageCondition = {
        previousStage: document.getElementById("previousStageTrigger").value,
        currentStage: document.getElementById("currentStageTrigger").value,
      };
    }


    if (actionType === "STATUS_CHANGE") {
      newRuleObject.statusCondition = {
        // previousStatus: document.getElementById("previousStatusAction").value,
        currentStatus: document.getElementById("nextStatusAction").value,
      };
    } else if (actionType === "STAGE_CHANGE") {
      newRuleObject.stageCondition = {
        // previousStage: document.getElementById("previousStatgeAction").value,
        currentStage: document.getElementById("nextStageAction").value,
      }

    }
    
    setRuleObject(newRuleObject);
    axios
      .post(`http://localhost:8080/projects/${projectId}/rules`,newRuleObject)
      .then((response) => {
        console.log(response.data.data);
      })
      .catch((error) => {
        console.error("Error fetching rules: ", error);
      });
    console.log(newRuleObject);
    handleClose();
  };

  let modalTitle, modalContent;
  modalTitle = "Add Rule";

  const renderTrigger = (
    <div>
      {triggerType === "STATUS_CHANGE" && (
        <div>
          <label>Previous Status</label>
          <select id="previousStatusTrigger" className="form-control">
            {status.map((status) => (
              <option value={status}>{status}</option>
            ))}
          </select>

          <label>current Status</label>
          <select id="currentStatusTrigger" className="form-control">
            {status.map((status) => (
              <option value={status}>{status}</option>
            ))}
          </select>
        </div>
      )}
      {triggerType === "STAGE_CHANGE" && (
        <div>
          <label>Previous Stage</label>
          <select id="previousStageTrigger" className="form-control">
            {stageList.map((stage) => (
              <option value={stage.stageId}>{stage.stageName}</option>
            ))}
          </select>

          <label>current Stage</label>
          <select id="currentStageTrigger" className="form-control">
            {stageList.map((stage) => (
              <option value={stage.stageId}>{stage.stageName}</option>
            ))}
          </select>
        </div>
      )}
      {/* Add more conditions for other trigger types if needed */}
    </div>
  );

  const renderAction = (
    <div>
      {actionType === "STATUS_CHANGE" && (
        <div>
          <label>Next Status</label>
          <select id="nextStatusAction" className="form-control">
            {status.map((status) => (
              <option value={status}>{status}</option>
            ))}
          </select>
        </div>
      )}
      {actionType === "STAGE_CHANGE" && (
        <div>
          <label>Next Stage</label>
          <select id="nextStageAction" className="form-control">
            {stageList.map((stage) => (
              <option value={stage.stageId}>{stage.stageName}</option>
            ))}
          </select>
        </div>
      )}
      {/* Add more conditions for other trigger types if needed */}
    </div>
  );



  modalContent = (
    <div>
      <div className="row">
        <div className="col">
          <label>Select Trigger Type:</label>
          <select className="form-control" 
          onChange={handleTriggerTypeChange}
          value={triggerType}
          >
            <option value="">Select Trigger Type</option>
            <option value="STATUS_CHANGE">STATUS_CHANGE</option>
            <option value="STAGE_CHANGE">STAGE_CHANGE</option>
          </select>
        </div>
        <div className="col">
          <label>Select Action Type:</label>
          <select className="form-control" 
          onChange={handleActionTypeChange}
          value={actionType}
          >
            <option value="">Select Action Type</option>
            <option value="STATUS_CHANGE">STATUS_CHANGE</option>
            <option value="STAGE_CHANGE">STAGE_CHANGE</option>
          </select>
        </div>
      </div>
      <div className="row">
        <div className="col">
          {renderTrigger}
        </div>
        <div className="col">
          {renderAction}
        </div>
      </div>
    </div >
  );

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{modalTitle}</Modal.Title>
      </Modal.Header>
      <Modal.Body>{modalContent}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateRule}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddRule;
