import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import axios from "axios";
import { useSelector } from "react-redux";

const AddRule = ({ show, handleClose }) => {

    const { projectById } = useSelector((state) => state.Project);
  const { status } = projectById;
  const { stageList } = projectById;

    const [formData, setFormData] = useState({
        triggerType: "Status_Change", // Default trigger type
        previousStage: "",
        currentStage: "",
        condition: "",
        nextStatus: "",
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };
    const handleCreateRule = () => {
        // API call to create rule with formData
        axios
            .post(`http://localhost:8080/projects/rules`, formData)
            .then((response) => {
                console.log("API call successful:", response.data.data);
            })
            .catch((error) => {
                console.error("Error updating data: ", error);
            });
        handleClose();
    };

    const renderTriggerOptions = () => {
        if (formData.triggerType === "Status_Change") {
            return (
                <div>
                    {/* Status Change specific options */}
                    <div className="form-group">
                        <label>Previous Status</label>
                        <select
              className="form-control"
              name="condition"
              value={formData.condition}
              onChange={handleInputChange}
            >
              <option key="option" value="option">
                Any
              </option>
              {status?.map((status) => (
                <option key={status} value={status}>
                  {stage}
                </option>
              ))}
              
            </select>
                        <label>Next Status</label>
                        <input
                            type="text"
                            className="form-control"
                            name="previousStage"
                            value={formData.previousStage}
                            onChange={handleInputChange}
                        />
                        
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        } else if (formData.triggerType === "Stage_Change") {
            return (
                <div>
                    {/* Stage Change specific options */}
                    <div className="form-group">
                        <label>Previous Stage</label>
                        <input
                            type="text"
                            className="form-control"
                            name="previousStage"
                            value={formData.previousStage}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Current Stage</label>
                        <input
                            type="text"
                            className="form-control"
                            name="currentStage"
                            value={formData.currentStage}
                            onChange={handleInputChange}
                        />
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        } else if (formData.triggerType === "Due_Date_Change") {
            return (
                <div>
                    {/* Due Date Change specific options */}
                    <div className="form-group">
                        <label>Condition</label>
                        <input
                            type="text"
                            className="form-control"
                            name="condition"
                            value={formData.condition}
                            onChange={handleInputChange}
                        />
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        }
        return null; // Return null if no matching trigger type
    };

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Rules</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="form-group">
                    <label>Trigger Type</label>
                    <select
                        className="form-control"
                        name="triggerType"
                        value={formData.triggerType}
                        onChange={handleInputChange}
                    >
                        <option value="Status_Change">Status Change</option>
                        <option value="Stage_Change">Stage Change</option>
                        <option value="Due_Date_Change">Due Date Change</option>
                    </select>
                </div>
                {renderTriggerOptions()}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={handleCreateRule}>
                    Create
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddRule;
