import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { DropdownButton, Dropdown } from "react-bootstrap";
import RuleModal from "../Rule/RuleModal";
import RuleModalForStage from "../Rule/RuleModalForStage";

const Header = () => {
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [showRuleModal, setShowRuleModal] = useState(false);
  const dispatch = useDispatch();

  const handleOptionSelect = (option) => {
    setShowTaskModal(option === "Rule for Stage");
    setShowRuleModal(option === "Show Rule");
  };

  const closeModal = () => {
    setShowTaskModal(false);
    setShowRuleModal(false);
  };

  return (
    <div className="header">
      <DropdownButton id="create-dropdown" title="Create" variant="primary">
        <Dropdown.Item onClick={() => handleOptionSelect("Rule for Stage")}>
          Rule for Stage
        </Dropdown.Item>
        <Dropdown.Item onClick={() => handleOptionSelect("Show Rule")}>
          Show Rule
        </Dropdown.Item>
      </DropdownButton>

      <button className="add-project" onClick={() => setShowTaskModal(true)}>
        Add Project
      </button>

      {showTaskModal && (
        <RuleModalForStage
          show={showTaskModal}
          handleClose={closeModal}
          // Add necessary props for RuleModalForStage component
        />
      )}

      {showRuleModal && (
        <RuleModal
          show={showRuleModal}
          handleClose={closeModal}
          // Add necessary props for RuleModal component
        />
      )}
    </div>
  );
};

export default Header;
