import React, { useEffect, useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";

const AddRule = ({ show, handleClose }) => {
  const { projects } = useSelector((state) => state.Project);
  const { projectById } = useSelector((state) => state.Project);
  const { status } = projectById;
  const { stageList } = projectById;
  const { projectId } = projectById;
  const [triggerType, setTriggerType] = useState("");
  const [actionType, setActionType] = useState("");
  const [selectedProjectId, setSelectedProjectId] = useState(); // State to store selected project ID
  const [stages, setStages] = useState([]);
  const [FieldList, setFieldList] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8080/projects/fields`)
      .then((response) => {
        setFieldList(response.data.data);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  }, []);

  const handleTriggerTypeChange = (e) => {
    setTriggerType(e.target.value);
    c
  };

  const handleActionTypeChange = (e) => {
    setActionType(e.target.value);
  };

  const handleCreateRule = () => {
    if (triggerType === "" || actionType === "") {
      return;
    }

    let newRuleObject = {
      triggerType: triggerType,
      actionType: actionType,
    };

    if (triggerType === "STATUS_CHANGE") {
      newRuleObject.statusCondition = {
        previousStatus: document.getElementById("previousStatusTrigger").value === "Any" ? null : document.getElementById("previousStatusTrigger").value,
        currentStatus: document.getElementById("currentStatusTrigger").value === "Any" ? null : document.getElementById("currentStatusTrigger").value,
      };
    } else if (triggerType === "STAGE_CHANGE") {
      newRuleObject.stageCondition = {
        previousStage: document.getElementById("previousStageTrigger").value === "Any" ? null : document.getElementById("previousStageTrigger").value,
        currentStage: document.getElementById("currentStageTrigger").value === "Any" ? null : document.getElementById("currentStageTrigger").value,
      };
    } else if (triggerType === "DUE_DATE_APPROACHING") {
      newRuleObject.triggerType = "DUE_DATE";
      newRuleObject.dateCondition = {
        operation: "before",
        day: document.getElementById("daysTrigger").value,
        hours: document.getElementById("hoursTrigger").value,
        minute: document.getElementById("minutesTrigger").value,
      };
    } else if (triggerType === "OVER_DUE") {
      newRuleObject.triggerType = "DUE_DATE";
      newRuleObject.dateCondition = {
        operation: "after",
        day: document.getElementById("daysTrigger").value,
        hours: document.getElementById("hoursTrigger").value,
        minute: document.getElementById("minutesTrigger").value,
      };
    } else if (triggerType === "PRORITY_CHANGE") {
      newRuleObject.prorityCondition = {
        previousPrority: document.getElementById("previousProrityTrigger").value === "Any" ? null : document.getElementById("previousProrityTrigger").value,
        currentPrority: document.getElementById("currentProrityTrigger").value === "Any" ? null : document.getElementById("currentProrityTrigger").value,
      };
    } else if (triggerType === "ADD_TO_PROJECT") {
      newRuleObject.addToProjectCondition = {
        stageId : document.getElementById("nextProjectStageCondition").value === "Any" ? null : document.getElementById("nextProjectStageCondition").value
      };
    }
    


    if (actionType === "STATUS_CHANGE_ACTION") {
      newRuleObject.statusAction = {
        // previousStatus: document.getElementById("previousStatusAction").value,
        currentStatus: document.getElementById("nextStatusAction").value,
      };
    } else if (actionType === "STAGE_CHANGE_ACTION") {
      newRuleObject.stageAction = {
        // previousStage: document.getElementById("previousStatgeAction").value,
        currentStage: document.getElementById("nextStageAction").value,
      }
    } else if (actionType === "ADD_TO_PROJECT_ACTION") {
      newRuleObject.addToProjectAction = {
        // previousStage: document.getElementById("previousStatgeAction").value,
        currentProject: document.getElementById("nextProjectAction").value,
        stageId: document.getElementById("nextProjectStageAction").value
      }
    } else if (actionType === "PRORITY_CHANGE_ACTION") {
      newRuleObject.prorityAction = {
        currentPrority: document.getElementById("nextProrityAction").value,
      };
    }
    console.log(newRuleObject);
    axios
      .post(`http://localhost:8080/projects/${projectId}/rules`, newRuleObject)
      .then((response) => {
        console.log("Response------", response.data.data);
      })
      .catch((error) => {
        console.error("Error fetching rules: ", error);
      });
    handleClose();
  };

  let modalTitle, modalContent;
  modalTitle = "Add Rule";

  const renderTrigger = (
    <div>
      {triggerType === "STATUS_CHANGE" && (
        <div>
          <label>Previous Status</label>
          <select id="previousStatusTrigger" className="form-control">
            <option value="Any">Any</option>
            {status.map((status) => (
              <option value={status}>{status}</option>
            ))}
          </select>
          <label>current Status</label>
          <select id="currentStatusTrigger" className="form-control">
            <option value="Any">Any</option>
            {status.map((status) => (
              <option value={status}>{status}</option>
            ))}
          </select>
        </div>
      )}
      {/* Add more conditions for other trigger types if needed */}
    </div>
  );

  const renderAction = (
    <div>
      {actionType === "STATUS_CHANGE_ACTION" && (
        <div>
          <label>Next Status</label>
          <select id="nextStatusAction" className="form-control">
            {status.map((status) => (
              <option value={status}>{status}</option>
            ))}
          </select>
        </div>
      )}
      
      {/* Add more conditions for other trigger types if needed */}
    </div>
  );


  useEffect(() => {
    if (selectedProjectId) {
      // Fetch stages for the selected project
      axios
        .get(`http://localhost:8080/projects/${selectedProjectId}/stages`)
        .then((response) => {
          setStages(response.data.data); // Assuming response.data contains the list of stages
        })
        .catch((error) => {
          console.error("Error fetching stages: ", error);
        });
    }
  }, [selectedProjectId]);

  modalContent = (
    <div>
      <div className="row">
        <div className="col">
          <label>Select Trigger Type:</label>
          <select className="form-control"
            onChange={handleTriggerTypeChange}
            value={triggerType}
          >
             <option value="">Select trigger Type</option>
            {FieldList.map((fields) => (
              <option value={fields.id}>{fields.name}</option>
            ))}
          </select>
        </div>
        <div className="col">
          <label>Select Action Type:</label>
          <select className="form-control"
            onChange={handleActionTypeChange}
            value={actionType}
          >
            <option value="">Select Action Type</option>
            {FieldList.map((fields) => (
              <option value={fields.id}>{fields.name}</option>
            ))}
          </select>
        </div>
      </div>
      <div className="row">
        <div className="col">
          {renderTrigger}
        </div>
        <div className="col">
          {renderAction}
        </div>
      </div>
    </div >
  );

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{modalTitle}</Modal.Title>
      </Modal.Header>
      <Modal.Body>{modalContent}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateRule}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddRule;
