    //   .then((response) => {
    //     console.log("Response------", response.data.data);
    //   })
    //   .catch((error) => {
    //     console.error("Error fetching rules: ", error);
    //   });
    handleClose();
  };

  let modalTitle, modalContent;
  modalTitle = "Add Rule";

  const renderTrigger = (
    <div>
      {triggerCondition === "STRING" && (
        <div>
             <option value="set">Set</option>
