import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { createTask } from "../../Api/AllApi";
import { addTicketToStage } from "../../slice/ProjectSlice";

const TaskModal = ({ show, handleClose , stageId }) => {
  const [taskName, setTaskName] = useState("");
  const [taskEndingDate, setTaskEndingDate] = useState("");
  const [taskAssigneeName, setTaskAssigneeName] = useState("");
  const [taskDescription, setTaskDescription] = useState("");
  const [taskStatus, setTaskStatus] = useState("");

  const { projectById } = useSelector((state) => state.Project);
  const dispatch = useDispatch();

  const handleCreateTask = () => {
    // if (!validateTaskName(taskName)) {
    //   console.error(
    //     "Invalid task name. Please use only letters and numbers, and start with a letter."
    //   );
    //   return;
    // }

    // const formattedStartingDate = formatDate(taskStartingDate); // Format the starting date
    // const formattedEndingDate = formatDate(taskEndingDate); // Format the ending date

    const newTask = {
      ticketName : taskName,
      taskStartingDate: formattedStartingDate,
      taskEndingDate: formattedEndingDate,
      ticketAssign : taskAssigneeName,
      ticketDescription : taskDescription,
      ticketStatus : taskStatus
    };
    console.log(newTask);

    createTask(projectById.projectId , stageId , newTask)
      .then((response) => {
        dispatch(addTicketToStage({ stageId : stageId ,  ticketData: response.data.data }));
        handleClose();
      })
      .catch((error) => {
        console.error("Error creating task: ", error);
      });

    setTaskName("");
    // setTaskStartingDate(getCurrentDate());
    // setTaskEndingDate("");
    setTaskAssigneeName("");
    setTaskDescription("");
    setTaskStatus("");
    handleClose();
  };

  // Helper function to get the current date in the "yyyy-MM-dd" format
  // const getCurrentDate = () => {
  //   const today = new Date();
  //   const year = today.getFullYear();
  //   let month = (today.getMonth() + 1).toString();
  //   if (month.length === 1) {
  //     month = `0${month}`;
  //   }
  //   let day = today.getDate().toString();
  //   if (day.length === 1) {
  //     day = `0${day}`;
  //   }
  //   return `${year}-${month}-${day}`;
  // };

  // Validation function for the task name
  // const validateTaskName = (name) => {
  //   const pattern = /^[a-zA-Z][a-zA-Z0-9]*$/;
  //   return pattern.test(name);
  // };

  // Helper function to format the date as "yyyy-MM-dd"
  // const formatDate = (date) => {
  //   if (!date) {
  //     return "";
  //   }
  //   const parts = date.split("-");
  //   if (parts.length === 3) {
  //     const year = parts[0];
  //     const month = parts[1];
  //     const day = parts[2];
  //     return `${year}-${month}-${day}`;
  //   }
  //   return "";
  // };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Add Task</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form>
          <div className="form-group">
            <label>
              Task Name <span style={{ color: "red" }}>*</span>
            </label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter task name"
              value={taskName}
              onChange={(e) => setTaskName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>
              Project <span style={{ color: "red" }}>*</span>
            </label>
            <input
              type="text"
              className="form-control"
              value={projectById.projectName}
              readOnly={true}
            />
          </div>
          <div className="form-group">
            <label>Start Date</label>
            <input
              type="date"
              className="form-control"
              // value={taskStartingDate}
              // onChange={(e) => setTaskStartingDate(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>End Date</label>
            <input
              type="date"
              className="form-control"
              // value={taskEndingDate}
              // onChange={(e) => setTaskEndingDate(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Assignee Name</label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter assignee name"
              value={taskAssigneeName}
              onChange={(e) => setTaskAssigneeName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Task Status</label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter task status"
              value={taskStatus}
              onChange={(e) => setTaskStatus(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Task Description</label>
            <textarea
              className="form-control"
              placeholder="Enter task description"
              rows="4"
              value={taskDescription}
              onChange={(e) => setTaskDescription(e.target.value)}
            />
          </div>
        </form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateTask}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default TaskModal;
