import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector } from "react-redux";
const RuleModal = ({ show, handleClose, projectId }) => {
    const { projectById } = useSelector((state) => state.Project);
  const [rules, setRules] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/projects/${projectId}/rules`)
      .then((response) => {
        setRules(response.data.data);
        console.log(response.data.data);
      })
      .catch((error) => {
        console.error("Error fetching rules: ", error);
      });
  }, [projectId]);
  console.log(projectById.stage);
  return (
    <div className={`modal ${show ? "show" : ""}`} style={{ display: "block" }}>
      <div className="rulemodal-dialog modal-lg">
        <div className="modal-content" style={{ width: "1200px", height: "600px" }}>
          <div className="modal-header">
            <h5 className="modal-title">Rules</h5>
            <button type="button" className="close" onClick={handleClose}>
              <span>&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Previous</th>
                  <th>Current</th>
                  <th>condition</th>
                  <th>action</th>
                  <th>TriggerType</th>
                </tr>
              </thead>
              <tbody>
                {rules.map((rule, index) => (
                  <tr key={index}>
                    <td>{rule.previousStageTrigger || "N/A"}</td>
                    <td>{rule.currentStageTrigger || "N/A"}</td>
                    <td>{rule.previousStageAction || "N/A"}</td>
                    <td>{projectById.stageList.find(stage=> stage.stageId == rule.currentStageAcion) || "N/A"}</td>
                    <td>{rule.triggerType || "N/A"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={handleClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
export default RuleModal;
