import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";
const RuleModalForStage = ({ show, handleClose }) => {
  const { projectById } = useSelector((state) => state.Project);
  const { status } = projectById;
  const { stageList } = projectById;
  const [formData, setFormData] = useState({
    previousStage: "",
    currentStage: "",
    condition: "",
    nextStatus: "",
  });
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleCreateRule = () => {
    const selectedStage = stageList.find(
      (stage) => stage.stageName === formData.nextStatus
    );

    const newRule = {
      previousStageTrigger: formData.previousStage || "Any",
      currentStageTrigger: formData.currentStage || "Any",
      previousStageAction: formData.condition || "Any",
      currentStageAcion: formData.nextStatus || "Any",
      triggerType: "StageChange",
    };

    const [rules, setRules] = useState([]);
    useEffect(() => {
      axios
        .get(`http://localhost:8080/projects/${projectId}/rules`)
        .then((response) => {
          setRules(response.data.data);
          console.log(response.data.data);
        })
        .catch((error) => {
          console.error("Error fetching rules: ", error);
        });
    }, [projectId]);
    if(newRule.currentStageAcion === "Any"){
      window.confirm(
        "Please Select Next Status "
      );
      return;
    }
    axios
      .post(
        `http://localhost:8080/projects/${projectById.projectId}/rules`,
        newRule
      )
      .then((response) => {
        console.log("API call successful:", response.data.data);
      })
      .catch((error) => {
        console.error("Error updating data: ", error);
      });
    handleClose();
  };
  let modalTitle, modalContent;
      modalTitle = "Rule for Stage";
      modalContent = (
        <div>
          <div className="modal-body">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>actionType</th>
                  <th>TriggerType</th>
                </tr>
              </thead>
              <tbody>
                {rules.map((rule, index) => (
                  <tr key={index}>
                    <td>{rule?.trigger.conditionType || "N/A"}</td>
                    <td>{rule.action.conditionType || "N/A"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={handleClose}>
              Close
            </button>
          </div>
        </div>
      );
  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{modalTitle}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {modalContent}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateRule}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};
export default RuleModalForStage;
