import React, { useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import WebSocketService from './WebSocketService'; // Import your WebSocket service
import Header from './Header';
import NavBar from './NavBar';
import Project from './Project';
import Stage from './Stage';

function App() {
    useEffect(() => {
        // Connect to WebSocket when the component mounts
        WebSocketService.connect();

        // Subscribe to WebSocket messages
        WebSocketService.subscribeToActions((message) => {
            console.log('Received WebSocket message in App component:', message);
            // Handle the WebSocket message as needed in your App component
        });

        // Clean up the WebSocket connection when the component unmounts
        return () => {
            WebSocketService.disconnect();
        };
    }, []);

    return (
        <div className="App mainComponent">
            <Routes>
                <Route path="projects/:projectId" element={<Project />} />
                <Route path="stages" element={<Stage />} />
            </Routes>
            <Header />
            <NavBar />
        </div>
    );
}

export default App;
