import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {  updateSliceTicket } from "../../slice/ProjectSlice";
import axios from "axios";

const Task = ({ taskIndex ,stageIndex ,stageId}) => {

  const { projectById } = useSelector((state) => state.Project);
  const task = projectById.stageList[stageIndex].ticketList[taskIndex];
  const dispatch = useDispatch();
  const { status } = projectById;

  const [formData, setFormData] = useState({
    ticketId: task.ticketId,
    ticketName: task.ticketName,
    assigneeName: task.ticketAssign,
    endDate: task.ticketEndingDate,
    ticketDescription: task.ticketDescription,
    status: task.status, 
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleStageChange = (e) => {
    const { name, value } = e.target;

    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleClose = () => {
    console.log(formData);
    axios.put(`http://localhost:8080/projects/${projectById.projectId}/stages/${stageId}/tickets`, formData)
    .then((response) => {
      dispatch(updateSliceTicket({
        previous:stageId,
        data:response.data.data
      }))
      console.log("Ticket updated successfully", response.data.data);
    })
    .catch((error) => {
      console.error("Error updating the ticket: ", error);
    });
  };
  const handelOpen = () => {
    axios.get(`http://localhost:8080/projects/${projectById.projectId}/stages/${stageId}/tickets/${task.ticketId}`)
    .then((response) => {
      console.log("ticket Bu ID", response.data.data);
      setFormData(response.data.data);
    })
    .catch((error) => {
      console.error("Error updating the ticket: ", error);
    });
  }

  return (
    <div className="card task-card">
      <button
      onClick={handelOpen}
        type="button"
        className="btn btn-primary"
        data-bs-toggle="offcanvas"
        data-bs-target={`#offcanvasRight_${task.ticketId}`}
        style={{ margin: "10px", backgroundColor: "blue" }}
      >
        <div className="card-title">
          <h4>{task.ticketName}</h4>
        </div>
      </button>

      <div
        className="offcanvas offcanvas-end"
        tabIndex="-1"
        id={`offcanvasRight_${task.ticketId}`}
      >
        <div className="offcanvas-header">
          <h2 className="offcanvas-title text-center">Ticket Details</h2>
          <button
            type="button"
            className="btn btn-close"
            data-bs-dismiss="offcanvas"
            onClick={handleClose}
          ></button>
        </div>

        <div className="offcanvas-body">
          <form className="ticket-form">
            <p>Task created on: {task.ticketStartingDate}</p>

            <div>
              <div className="form-group">
                <label htmlFor="ticketName">Ticket Name</label>
                <input
                  type="text"
                  name="ticketName"
                  id="ticketName"
                  className="form-control"
                  value={formData.ticketName}
                  onChange={handleChange}
                />
              </div>

              {/* Add a select dropdown for selecting the stage */}
              <div className="form-group">
                <label htmlFor="selectedStage">Select Status</label>
                <select
                  name="status"
                  id="status"
                  className="form-control"
                  value={formData.status}
                  onChange={handleStageChange}
                >
                  {status?.map((stage) => (
                    <option key={stage} value={stage}>
                      {stage}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="assigneeName">Assignee Name</label>
                <input
                  type="text"
                  name="assigneeName"
                  id="assigneeName"
                  className="form-control"
                  value={formData.assigneeName}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="endDate">End Date</label>
                <input
                  type="date"
                  name="endDate"
                  id="endDate"
                  className="form-control"
                  value={formData.endDate}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="ticketDescription">Ticket Description</label>
                <textarea
                  name="ticketDescription"
                  id="ticketDescription"
                  className="form-control"
                  value={formData.ticketDescription}
                  onChange={handleChange}
                />
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Task;
