import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import axios from "axios";
import { useSelector } from "react-redux";

const AddRule = ({ show, handleClose }) => {

    const { projectById } = useSelector((state) => state.Project);
    const { status } = projectById;
    const { stageList } = projectById;

    // const [formData, setFormData] = useState({
    //     triggerType: "Status_Change",
    //     previousTrigger: "",
    //     currentTrigger: "",
    //     actionType: "",
    //     action: "",
    // });

    // const handleInputChange = (e) => {
    //     const { name, value } = e.target;
    //     setFormData((prevData) => ({
    //         ...prevData,
    //         [name]: value,
    //     }));
    // };
    const handleCreateRule = () => {
        // API call to create rule with formData
        // if (formData.triggerType === "Due_Date_Approcharging") {
        //     formData.triggerType = "Date_Seduled";
        //     formData.previousTrigger = "before";
        // }
        // if (formData.triggerType === "Task_OverDue") {
        //     formData.triggerType = "Date_Seduled";
        //     formData.previousTrigger = "after";
        // }
        // if(formData.actionType === "Change_Stage"){
        //     const selectedStage = stageList.find(
        //         (stage) => stage.stageName === formData.action
        //       );
        //     const stageId = selectedStage?.stageId;
        //     formData.action=stageId;
        // }
        
        axios
        .post(
          `http://localhost:8080/projects/${projectById.projectId}/rules`,
          formData
        )
        .then((response) => {
          console.log("API call successful:", response.data.data);
        })
        .catch((error) => {
          console.error("Error updating data: ", error);
        });
        setFormData({
            triggerType: "Status_Change",
            previousTrigger: "",
            currentTrigger: "",
            actionType: "",
            action: "",
        });
        handleClose();
    };

    const renderTriggerOptions = () => {
        if (formData.triggerType === "Status_Change") {
            return (
                <div>
                    {/* Status Change specific options */}
                    <div className="form-group">
                        <label>Previous Status</label>
                        <select
                            className="form-control"
                            name="previousTrigger"
                            value={formData.previousTrigger}
                            onChange={handleInputChange}
                        >
                            <option key="option" value="option">
                                Any
                            </option>
                            {status?.map((status) => (
                                <option key={status} value={status}>
                                    {status}
                                </option>
                            ))}

                        </select>
                        <label>Next Status</label>
                        <select
                            className="form-control"
                            name="currentTrigger"
                            value={formData.currentTrigger}
                            onChange={handleInputChange}
                        >
                            <option key="option" value="option">
                                Any
                            </option>
                            {status?.map((status) => (
                                <option key={status} value={status}>
                                    {status}
                                </option>
                            ))}

                        </select>
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        } else if (formData.triggerType === "Stage_Change") {
            return (
                <div>
                    {/* Stage Change specific options */}
                    <div className="form-group">
                        <label>Previous Stage</label>
                        <select
                            className="form-control"
                            name="previousTrigger"
                            value={formData.previousTrigger}
                            onChange={handleInputChange}
                        >
                            <option key="Any" value="Any">
                                Any
                            </option>
                            {stageList?.map((stage) => (
                                <option key={stage.stageId} value={stage.stageName}>
                                    {stage.stageName}
                                </option>
                            ))}
                        </select>

                    </div>
                    <div className="form-group">
                        <label>Current Stage</label>
                        <select
                            className="form-control"
                            name="action"
                            value={formData.action}
                            onChange={handleInputChange}
                        >
                            <option key="Any" value="Any">
                                Any
                            </option>
                            {stageList?.map((stage) => (
                                <option key={stage.stageId} value={stage.stageName}>
                                    {stage.stageName}
                                </option>
                            ))}
                        </select>
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        } else if (formData.triggerType === "Due_Date_Approcharging") {
            return (
                <div>
                    {/* Due Date Change specific options */}
                    <div className="form-group">
                        <label>Day before</label>
                        <input
                            type="number"
                            className="form-control"
                            name="currentTrigger"
                            value={formData.currentTrigger}
                            onChange={handleInputChange}
                            min="1"
                        />
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        } else if (formData.triggerType === "Task_OverDue") {
            return (
                <div>
                    <div className="form-group">
                        <label>Day After</label>
                        <input
                            type="number"
                            className="form-control"
                            name="currentTrigger"
                            value={formData.currentTrigger}
                            onChange={handleInputChange}
                            min="1"
                        />
                    </div>
                </div>
            );
        }
        return null; // Return null if no matching trigger type
    };

    const renderActionOptions = () => {
        if (formData.actionType === "Change_Status") {
            return (
                <div>
                    {/* Status Change specific options */}
                    <div className="form-group">
                        <label>Next Status</label>
                        <select
                            className="form-control"
                            name="action"
                            value={formData.action}
                            onChange={handleInputChange}
                        >
                            <option key="option" value="option">
                                Any
                            </option>
                            {status?.map((status) => (
                                <option key={status} value={status}>
                                    {status}
                                </option>
                            ))}

                        </select>
                    </div>
                </div>
            );
        } else if (formData.actionType === "Change_Stage") {
            return (
                <div>
                    {/* Stage Change specific options */}
                    <div className="form-group">
                        <label>Next Stage</label>
                        <select
                            className="form-control"
                            name="action"
                            value={formData.action}
                            onChange={handleInputChange}
                        >
                            <option key="Any" value="Any">
                                Any
                            </option>
                            {stageList?.map((stage) => (
                                <option key={stage.stageId} value={stage.stageName}>
                                    {stage.stageName}
                                </option>
                            ))}
                        </select>
                    </div>
                    {/* ... add more fields */}
                </div>
            );
        }
        return null; // Return null if no matching trigger type
    };



    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Rules</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="row">
                    <div className="col-md-6">
                        <div className="form-group">
                            <label>Trigger Type</label>
                            <select
                                className="form-control"
                                name="triggerType"
                                value={formData.triggerType}
                                onChange={handleInputChange}
                            >
                                <option value="Status_Change">Status Change</option>
                                <option value="Stage_Change">Stage Change</option>
                                <option value="Due_Date_Approcharging">Due_Date_Approcharging</option>
                                <option value="Task_OverDue">Task_OverDue</option>
                            </select>
                            {renderTriggerOptions()}
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="form-group">
                            <label>Action Type</label>
                            <select
                                className="form-control"
                                name="actionType"
                                value={formData.actionType}
                                onChange={handleInputChange}
                            >
                                <option value="Change_Status">Status Change</option>
                                <option value="Change_Stage">Stage Change</option>
                            </select>
                            {renderActionOptions()}
                        </div>
                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={handleCreateRule}>
                    Create
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddRule;
