import React, { useState, useEffect } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";

const AddRule = ({ show, handleClose }) => {
  const { projectById } = useSelector((state) => state.Project);
  const { projectId } = projectById;

  const [triggerCondition, setTriggerCondition] = useState("set");
  const [actionCondition, setActionCondition] = useState("set");
  const [fieldTrigger, setFieldTrigger] = useState("");
  const [fieldAction, setFieldAction] = useState("");
  const [conditionTrigger, setConditionTrigger] = useState("");
  const [conditionAction, setConditionAction] = useState("");
  const [fieldValue, setFieldValue] = useState("");
  const [previousState, setPreviousState] = useState("");
  const [currentState, setCurrentState] = useState("");
  const [days, setDays] = useState("");
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");

  const[fieldList,setFieldList] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8080/projects/fields`)
      .then((response) => {
        setFieldList(response.data.data);
        console.log(response.data.data);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  }, []);
  const handleDaysChange = (e) => {
    setDays(e.target.value);
  };

  const handleHoursChange = (e) => {
    setHours(e.target.value);
  };

  const handleMinutesChange = (e) => {
    setMinutes(e.target.value);
  };
  const handleOpenModal = () => {
    setTriggerCondition("set");
    setActionCondition("set");
  };

  const handleFieldTriggerChange = (e) => {
    const selectedValue = e.target.value;
    setFieldTrigger(selectedValue);

    if (
      selectedValue === "STATUS_CHANGE" ||
      selectedValue === "STAGE_CHANGE" ||
      selectedValue === "PRORITY_CHANGE" ||
      selectedValue === "ADD_TO_PROJECT"
    ) {
      setConditionTrigger("set");
    } else if (selectedValue === "DUE_DATE_APPROACHING") {
      setConditionTrigger("on");
    } else if (selectedValue === "OVER_DUE") {
      setConditionTrigger("before");
    }
  };

  const handleFieldActionChange = (e) => {
    const selectedValue = e.target.value;
    setFieldAction(selectedValue);

    if (
      selectedValue === "STATUS_CHANGE" ||
      selectedValue === "STAGE_CHANGE" ||
      selectedValue === "PRORITY_CHANGE" ||
      selectedValue === "ADD_TO_PROJECT"
    ) {
      setConditionAction("set");
    } else if (selectedValue === "DUE_DATE_APPROACHING") {
      setConditionAction("on");
    } else if (selectedValue === "OVER_DUE") {
      setConditionAction("before");
    }  if (selectedValue === "PERCENTAGE") {
      setConditionTrigger("less than");
    }  if (selectedValue === "PERCENTAGE") {
      setConditionAction("less than");
    }else {
      setConditionAction("less than");
    }
  };

  const handleConditionTriggerChange = (e) => {
    setConditionTrigger(e.target.value);
  };

  const handleConditionActionChange = (e) => {
    setConditionAction(e.target.value);
  };

  const handleFieldValueChange = (e) => {
    setFieldValue(e.target.value);
  };

  const handlePreviousStateChange = (e) => {
    setPreviousState(e.target.value);
  };

  const handleCurrentStateChange = (e) => {
    setCurrentState(e.target.value);
  };

  const renderTableHeader = () => {
    return (
      <div>
        <div className="row">
          <div className="col-5 mb-5">
            <strong>FIELD_TRIGGER</strong>
            <select
              className="form-control"
              onChange={handleFieldTriggerChange}
              value={fieldTrigger}
              id="fieldTrigger"
            >

             <option value="">Select FIELD_TRIGGER</option>
               {fieldList?.map((fields) => (
              <option value={fields?.id}>{fields?.name}</option>
            ))}
            </select>
          </div>
          <div className="col-5 mb-5">
            <strong>FIELD_ACTION</strong>
            <select
              className="form-control"
              onChange={handleFieldActionChange}
              value={fieldAction}

            >
               <option value="">Select FIELD_TRIGGER</option>
               {fieldList?.map((fields) => (
              <option value={fields?.id}>{fields?.name}</option>
            ))}
            </select>
          </div>
        </div>
                </div>
    );
  };

  const handleCreateRule = () => {
    // if (triggerType === "" || actionType === "") {
    //   return;
    // }

    const newRuleObject = {
      triggerField: fieldTrigger,
      triggerCondition:null,
      actionCondition:null,
      actionType: null,
      trigger: null,
      actionField:fieldAction
    };


    if(triggerCondition == "STRING"){
      const stringAction = {
        operation:null,
        previousString:null,
        currentString:null,
      }

    }
    newRuleObject.triggerCondition = triggerCondition;
    newRuleObject.actionCondition = actionCondition;

    
    console.log(newRuleObject);
    // axios
    //   .post(`http://localhost:8080/projects/${projectId}/rules`, newRuleObject)
    //   .then((response) => {
    //     console.log("Rule created successfully:", response.data);
    //     handleClose();
    //   })
    //   .catch((error) => {
    //     console.error("Error creating rule: ", error);
    //   });
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Add Rule</Modal.Title>
      </Modal.Header>
      <Modal.Body>{renderTableHeader()}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateRule}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddRule;
