import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";
const RuleModalForStatus = ({ show, handleClose }) => {
  const { projectById } = useSelector((state) => state.Project);
  const { status } = projectById;
  const { stageList } = projectById;
  const [formData, setFormData] = useState({
    previousStage: "",
    currentStage: "",
    condition: "",
    nextStage: "",
  });
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleCreateRule = () => {
    const selectedStage = stageList.find(
      (stage) => stage.stageName === formData.nextStage
    );
    const StageId = selectedStage?.stageId;
    console.log("StageId:", StageId);
    
    const newRule = {
      previousStageTrigger: formData.previousStage ,
      currentStageTrigger: formData.currentStage ,
      previousStageAction: formData.condition ,
      currentStageAcion: StageId ,
      triggerType: "StatusChange",
    };
    axios
      .post(
        `http://localhost:8080/projects/${projectById.projectId}/rules`,
        newRule
      )
      .then((response) => {
        console.log("API call successful:", response.data.data);
      })
      .catch((error) => {
        console.error("Error updating data: ", error);
      });
    handleClose();
  };
  let modalTitle, modalContent;
      modalTitle = "Rule for Status";
      modalContent = (
        <div>
          <div className="form-group">
            <label>Previous Stage</label>
            <select
              className="form-control"
              name="previousStage"
              value={formData.previousStage}
              onChange={handleInputChange}
            >
              <option key="Any" value="Any">
                Any
              </option>
              {status?.map((stage) => (
                <option key={stage} value={stage}>
                  {stage}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Current Stage</label>
            <select
              className="form-control"
              name="currentStage"
              value={formData.currentStage}
              onChange={handleInputChange}
            >
              <option key="Any" value="Any">
                Any
              </option>
              {status?.map((stage) => (
                <option key={stage} value={stage}>
                  {stage}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Condition</label>
            <select
              className="form-control"
              name="condition"
              value={formData.condition}
              onChange={handleInputChange}
            >
              <option key="Any" value="Any">
                Any
              </option>
              {stageList?.map((stage) => (
                <option key={stage.stageId} value={stage.stageName}>
                  {stage.stageName}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Change Status</label>
            <select
              className="form-control"
              name="nextStage"
              value={formData.nextStage}
              onChange={handleInputChange}
            >
              {stageList?.map((stage) => (
                <option key={stage.stageId} value={stage.stageName}>
                  {stage.stageName}
                </option>
              ))}
            </select>
          </div>
        </div>
      );
  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{modalTitle}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {modalContent}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateRule}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};
export default RuleModalForStatus;
