import React, { useEffect, useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";
const RuleModal = ({ show, handleClose }) => {
  const { projectById } = useSelector((state) => state.Project);
  const { stageList } = projectById;

  const {projectId} = projectById;
  const [isRightCanvasVisible, setIsRightCanvasVisible] = useState(false);

  const handleCreateRule = () => {
    setIsRightCanvasVisible(true);
  };


  const [rules, setRules] = useState([]);
  useEffect(() => {
    axios
      .get(`http://localhost:8080/projects/${projectId}/rules`)
      .then((response) => {
        setRules(response.data.data);
        console.log(response.data.data);
      })
      .catch((error) => {
        console.error("Error fetching rules: ", error);
      });
  }, [projectId]);

  let modalTitle, modalContent;
      modalTitle = "Rules ";
      modalContent = (
        <div>
          <div className="modal-body">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>actionType</th>
                  <th>TriggerType</th>
                </tr>
              </thead>
              <tbody>
                {rules.map((rule, index) => (
                  <tr key={index}>
                    <td>{rule?.trigger.conditionType || "N/A"}</td>
                    <td>{rule.action.conditionType || "N/A"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
  return (
    <div>
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{modalTitle}</Modal.Title>
        
      </Modal.Header>
      <Modal.Body>
        {modalContent}
      </Modal.Body>
      <Modal.Footer>
      <Button variant="primary" onClick={handleCreateRule}>
          Add Rule
        </Button>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
    </div>
  );
};
export default RuleModal;
