import React, { useState, useEffect } from "react";
import axios from "axios";
const RuleModal = ({ show, handleClose, projectId }) => {
  
    const selectedStage = stageList.find(
        (stage) => stage.stageName === formData.nextStatus
      );
  
      const newRule = {
        previousStageTrigger: formData.previousStage || "Any",
        currentStageTrigger: formData.currentStage || "Any",
        previousStageAction: formData.condition || "Any",
        currentStageAcion: formData.nextStatus || "Any",
        triggerType: "StageChange",
      };
  
      
      if(newRule.currentStageAcion === "Any"){
        window.confirm(
          "Please Select Next Status "
        );
        return;
      }
      axios
        .post(
          `http://localhost:8080/projects/${projectById.projectId}/rules`,
          newRule
        )
        .then((response) => {
          console.log("API call successful:", response.data.data);
        })
        .catch((error) => {
          console.error("Error updating data: ", error);
        });
      handleClose();
      
  return (
    <div className={`modal ${show ? "show" : ""}`} style={{ display: "block" }}>
      <div className="rulemodal-dialog modal-lg">
        <div className="modal-content" style={{ width: "1200px", height: "600px" }}>
          <div className="modal-header">
            <h5 className="modal-title">Rules</h5>
            <button type="button" className="close" onClick={handleClose}>
              <span>&times;</span>
            </button>
          </div>
          
        </div>
      </div>
    </div>
  );
};
export default RuleModal;
