import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { useSelector } from "react-redux";
import axios from "axios";
const AddRule = ({ show, handleClose }) => {
  const { projectById } = useSelector((state) => state.Project);
  const { status } = projectById;
  const { stageList } = projectById;

  let modalTitle, modalContent;
  modalTitle = "Add Rule";
  modalContent = (
    <div className="row">
      <div className="col">
        <label>Select Trigger Type:</label>
        <select
          className="form-control"
        >
          <option value="">Select Trigger Type</option>
          <option value="triggerType1">STATUS_CHANGE</option>
          <option value="triggerType2">STAGE_CHANGE</option>
        </select>
      </div>
      <div className="col">
        <label>Select Action Type:</label>
        <select
          value={actionType}
          // onChange={handleInputChange}
          className="form-control"
        >
          <option value="">Select Action Type</option>
          <option value="specificActionType">STATUS_CHANGE</option>
          <option value="anotherActionType">STAGE_CHANGE</option>
        </select>
      </div>
    </div>
  );
  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{modalTitle}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {modalContent}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleCreateRule}>
          Create
        </Button>
      </Modal.Footer>
    </Modal>
  );
};
export default AddRule;
